INSERT INTO posts_post VALUES(49,replace(replace('Приходи ко мне, когда я сплю,\r\nПоправляй одьяло осторожно,\r\nНаяву я не сказал «люблю»,\r\nА во сне без слов признаться можно.\r\nТы - неяркий чуть заметный свет,\r\nВ полуночном мареве разлитый,\r\nЯ проснусь, тебя как будто нет,\r\nВ пустоту мои глаза открыты.\r\nТак зачем, вернее для чего,\r\nТы сияньем легким сердце метишь,\r\nКаждым вздохом и касаньем светишь,\r\nА в ответ не просишь ничего?','\r',char(13)),'\n',char(10)),'2020-03-13 08:02:16.433809',3,2,NULL,0,0);
INSERT INTO posts_post VALUES(50,replace(replace('Нет времени задуматься, вздохнуть,\r\nХоть ненадолго, но остановиться,\r\nОн не из тех, кто в зеркало глядится\r\nИ верит в духов и ночную жуть.\r\nНабросив быстро на ходу пальто,\r\nОн за порог уверенно выходит,\r\nОтвет на всё он без труда находит,\r\nНет времени три года ни на что,\r\nНе может спорить, ссориться стареть\r\nИ завести собаку или кошку,\r\nС такою спешкой даже умереть\r\nИ то непозволительная роскошь.\r\nНапрасно ждёт, когда погаснет свет,\r\nИ он во тьме забудется немного…\r\nВедь там, где не кончается дорога,\r\nНет времени… и ожиданья нет','\r',char(13)),'\n',char(10)),'2020-03-13 08:02:52.328603',7,2,NULL,0,0);
