INSERT INTO posts_post VALUES(82,'В пятницу, 3 апреля, губернатор региона Дмитрий Азаров провел встречу с представителями самарских IT-компаний в формате видеоконференцсвязи. Онлайн обсуждались вопросы сотрудничества бизнеса и власти в борьбе против распространения инфекции коронавируса. Всего в совещании участвовало более 30 руководителей IT-компаний региона.','2020-04-07 08:09:40.011301',8,1,'posts/841c4f28-aafc-4d9d-8f6c-01714ffe914a.jpg',1,2);
