INSERT INTO posts_post VALUES(84,replace(replace('Эта история произошла в самом конце 90-х годов. Одно государство сломали, другое еще не построили. Ломка сознания происходила с трудом. Фабрики и заводы закрывались, Выплату зарплаты задерживали по нескольку месяцев, а если и платили, то по 20 % от положенного.\r\n\r\nНо люди продолжали работать и надеялись на лучшее. И верили в то, что все наладится. Как сводили концы с концами сейчас уже никто и не помнит. Но у каждого из этого времени есть своя, запомнившаяся ему история.','\r',char(13)),'\n',char(10)),'2020-04-08 15:37:43.728461',7,NULL,'posts/1534402487-stolica-s-su-lajk.jpg',2,7);
